children.default:
function () {
  let format;

  if (!this.series.chart.styledMode) {
      format = '<span style="color:' + this.point.color +
          '">● </span>';
  } else {
      format = '<span>● </span>';
  }
  format += '<span>' + (this.key || '') + '</span><br/>' +
      (this.point.label || '');
  return format;
}
